/* eslint-disable */
import React from 'react';
import { Link } from 'react-router-dom';
import { validateAll } from 'indicative';

class Signup extends React.Component{
    constructor(){
        super();


        this.state = {
            name: '',
            email: '',
            password: '',
            passwordConfirm: ''
        }

        this.handleInputChange = this.handleInputChange.bind(this)
        this.handleSubmitChange = this.handleSubmitChange.bind(this)


    }

    handleInputChange(event) {
        this.setState({
            [event.target.name]: event.target.value
        });
    }

    handleSubmitChange(event) {
        event.preventDefault()
        console.log(this.state)

        //validating user data

        const data = this.state;
        const rules = {
            name: 'required|string',
            email: 'required|email',
            password: 'required|min:6'

        };

        validateAll(data, rules)
        .then(()=>{})
        .catch(errors => {
            console.log(errors);
        })
    }

    render(){
        return (
            <div>
                <div className="container">
                    <div className="card o-hidden border-0 shadow-lg my-5">
                        <div className="card-body p-0">
                            {/* Nested Row within Card Body */}
                            <div className="row">
                                <div className="col-lg-5 d-none d-lg-block bg-register-image" />
                                <div className="col-lg-7">
                                    <div className="p-5">
                                        <div className="text-center">
                                            <h1 className="h4 text-gray-900 mb-4">Create an Account!</h1>
                                        </div>
                                        <form className="user" onSubmit={this.handleSubmitChange}>
                                            <div className="form-group">                                                
                                                <input type="text" name="name" onChange={this.handleInputChange} className="form-control form-control-user" id="username" placeholder="User Name" />                                                                                                
                                            </div>                                            
                                            <div className="form-group">                                                
                                                <input type="text" name="email" onChange={this.handleInputChange} className="form-control form-control-user" id="email" placeholder="Email" />                                                                                                
                                            </div>                                            
                                            <div className="form-group row">
                                                <div className="col-sm-6 mb-3 mb-sm-0">
                                                    <input type="password" name="password" onChange={this.handleInputChange} className="form-control form-control-user" id="Password" placeholder="Password" />
                                                </div>
                                                <div className="col-sm-6">
                                                    <input type="password" name="passwordConfirm" onChange={this.handleInputChange} className="form-control form-control-user" id="RepeatPassword" placeholder="Repeat Password" />
                                                </div>
                                            </div>
                                            <button type="submit" className="btn btn-primary btn-user btn-block">
                                                Register Account
              </button>
                                            <hr />
                                            <a href="index.html" className="btn btn-google btn-user btn-block">
                                                <i className="fab fa-google fa-fw" /> Register with Google
              </a>
                                            <a href="index.html" className="btn btn-facebook btn-user btn-block">
                                                <i className="fab fa-facebook-f fa-fw" /> Register with Facebook
              </a>
                                        </form>
                                        <hr />
                                        <div className="text-center">
                                            <Link className="small" to="/forgot-password"> Forgot Password? </Link>
                                        </div>
                                        <div className="text-center">
                                            <Link className="small" to="/login">Already have an account? Login!</Link>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
export default Signup;
