const affirmations = ['yes', 'true', 'y', 'ok', 'okay'];
/**
 *  Returns true if the `input` is one of the affirmative keywords.
 *  Below is the list of case insensitive keywords except `A`.
 *
 *  - 'yes'
 *  - 'true', treu
 *  - 'y'
 *  - 'ok'
 *  - 'okay'
 *  - 'A'
 *
 * @example
 * ```js
 * const { is } = require('indicative')
 *
 * if (is.affirmative('y')) {
 * }
 * ```
 */
exports.affirmative = (input) => {
    if (input === 'A' || input === true) {
        return true;
    }
    return affirmations.indexOf(input.toLowerCase()) > -1;
};
