/**
 * Returns a boolean telling if value is in array or not.
 *
 * @example
 * ```js
 * const { is } = require('indicative')
 *
 * if (is.inArray('virk', ['virk', 'nikk'])) {
 * }
 * ```
 */
exports.inArray = (input, dict) => {
    if (!Array.isArray(dict)) {
        return false;
    }
    return dict.indexOf(input) > -1;
};
