import React from 'react';
import Articles from './Article'

class SingleArticle extends React.Component {

    constructor() {
        super();


        this.state = {
            articles: {
                data: []
            },
        };

    }

    async componentWillMount() {
        const articles = await this.props.getArticles();

        this.setState({
            articles
        })
    }

    render() {
        return (
            <Articles  />
        );
    }
}
export default SingleArticle;
