import React from 'react';
import Articles from './Articles';
import Banner from '../Banner';

class Welcome extends React.Component {
    constructor() {
        super();


        this.state = {
            articles: {
                data:[]
            },            
        };

    }

    async componentWillMount(){
        const articles = await this.props.getArticles();

        this.setState({
            articles
        })
    }


    handlePagination = async (event) => {

        this.setState({
            [event.target.name]: event.target.type === 'file' ? event.target.files[0] : event.target.value
        });
    }


    render() {
        return (
            <div>
                <Banner
                    backgroundImage={`url(${process.env.PUBLIC_URL}assets/img/home-bg.jpg)`}
                    title="New React Blog"
                    subTitle="Welcome to App"
                />
                <Articles articless={this.state.articles.data} />
            </div>
        );
    }

}

export default Welcome;
