const alphaRegex = /^[a-z]+$/i;
/**
 * Returns a boolean telling if value passes the alpha test or not.
 *
 * @example
 * ```js
 * const { is } = require('indicative')
 *
 * if (is.alpha('hello')) {
 * }
 * ```
 */
exports.alpha = (input) => {
    return typeof (input) === 'string' && alphaRegex.test(input);
};
